package phanann_noem_CH3_HW1;

public class number1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i, fact = 1;
     	int num = 5;
    	for(i = 1; i <= num; i++) {
		fact=fact*i;
		}
	System.out.println("Factorial number of " + num + " is: " + fact);
		
	}

}
